import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  public mensagem: string = 'Olá angular!';
  public nome: string = 'Fulano';
  public idade: number = 20;
  public profissao: string = 'DEV Full Stack';


  public listaAnimais = [{id: "1", nome: "Billy", raca: "DNV", peso: 4},
                         {id: "2", nome: "Dora", raca: "DNV", peso: 4}]
}
